import React from "react";
import "./Main_Top.css";

function Main_Top() {
  return (
    <div
      className="main-top"
      style={{
        backgroundImage: "url('/about-banner.png')",
        backgroundSize: 'cover',
        backgroundPosition: 'center',
        height: '100vh', // 화면 전체 높이
        width: '100%',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        color: 'white', // 글씨 보이게
      }}
    >
              <h1>이야기의 세계로 건너가는 방법</h1>
		</div>
  );
}

export default Main_Top;
