import React from "react";
import styled from "./Main_Top.module.css";

function Main_Top() {
  return (
    <div className={styled.main_top}>
      {/* 찻번째 칸 */}
      <div
        className={styled.content_field_1}
        style={{
          backgroundImage: "url('/about-banner.png')",
          backgroundSize: "cover",
          backgroundPosition: "center",
          height: "640px",
          width: "100%",
          display: "flex",
        }}
      >
        <img className={styled.title} src="/about-banner-title.png" />
      </div>

      {/* 두번째 칸 */}
      <div className={styled.content_field_2}>
        <img className={styled.logo} src="/about-logo.png" width="200" />
        <div className={styled.text_md}>
          브릿G는
          <br />
          이야기의 가치를 더해 온 사람들이 만들어나가는
          <br /> 
          온라인 소설 플랫폼입니다.
        </div>
      </div>

      <div className={styled.content_field_3}>
        <div>
          <img src="/about-style.png" alt="브릿지 설명" width="219" />
        </div>
      </div>
    </div>
  );
}

export default Main_Top;
